figure.artwork:

enum { client, server } ClientOrServerExtension;

enum { RawPublicKey(0), (255) } FeatureType;

struct {
   select(ClientOrServerExtension) {
      case client:
         FeatureType supported-feature<1..2^8-1>;
      case server:
         FeatureType supported-feature;
   }
} ExtensionType;

