enum {
     cert(1), cert_req(2) (255)
} CachedInformationType;

struct {
     select (type) {
       case client: 
         CachedInformationType type;
         opaque hash_value<1..255>;
       case server: 
         CachedInformationType type; 
     } body;
} CachedObject;

struct {
     CachedObject cached_info<1..2^16-1>;
} CachedInformation;


